import './featured.css';
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

// Components
// import Product from '../../../components/Product/Product';

// Actions
import { getProductsActions } from '../../redux/actions/productActions';

const Featured = () => {

  const dispatch = useDispatch();

  const getProducts = useSelector((state) => state.getProducts);

  const {products, loading, error } = getProducts;

  useEffect(() => {
    dispatch(getProductsActions());
  }, [dispatch]);

  return (
    <div className="grid-wrapper">
      <div className="subheader-container">
        <h2 style={{fontSize: "2.375rem"}}>
          Featured Items
        </h2>
      </div>
      <div className="featured-content-container">
        <div className="col">
          <a href="https://www.mongodb.com/cloud/atlas" style={{textDecoration: 'none'}}>
            <img src={"https://images.unsplash.com/photo-1491553895911-0055eca6402d?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"} alt="temporary alt text"/>
            <div className="featured-description">
              <div className="featured-name">
                <b>Product Name</b>
              </div>
              <div className="featured-price">
                <p>Starting at $999.99</p>
              </div>
            </div>
          </a>
          {/* <Product /> */}
        </div>
        <div className="col">
          <a href="https://www.mongodb.com/cloud/atlas" style={{textDecoration: 'none'}}>
            <img src={"https://images.unsplash.com/photo-1572635196237-14b3f281503f?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"} alt="temporary alt text"/>
            <div className="featured-description">
              <div className="featured-name">
                <b>Product Name</b>
              </div>
              <div className="featured-price">
                <p>Starting at $999.99</p>
              </div>
            </div>
          </a>
          {/* <Product /> */}
        </div>
        <div className="col">
          <a href="https://www.mongodb.com/cloud/atlas" style={{textDecoration: 'none'}}>
            <img src={"https://images.unsplash.com/photo-1532413992378-f169ac26fff0?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"} alt="temporary alt text"/>
            <div className="featured-description">
              <div className="featured-name">
                <b>Product Name</b>
              </div>
              <div className="featured-price">
                <p>Starting at $999.99</p>
              </div>
            </div>
          </a>
          {/* <Product /> */}
        </div>
      </div>
    </div>
  )
}

export default Featured
