import './productPage.css'
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

// Components
import Button from '../../components/Button/Button.js'

// Actions


const ProductPage = () => {



  return (
    <div className="product-page-wrapper">
      <div className="product-page-container">
        <div className="product-page-image-container">
          {/* <img src={this.props.productData.image} style={{width:"70%"}} alt="temporary alt text"/> */}
          <img src="https://images.unsplash.com/photo-1607522370275-f14206abe5d3?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=708&q=80" style={{width:"70%"}} alt="temporary alt text"/>
        </div>
        <div className="product-page-product-description-container">
          <div className="product-page-title">
            {/* <b>
              {this.props.productData.title}
            </b> */}
            <b>
              Show Title
            </b>
          </div>
          <div className="product-page-price">
            {/* <b>
              From ${this.props.productData.price}
              <span style={{color:'grey', textDecoration:'line-through', marginLeft:'20px'}}>
                ${this.props.productData.price * 2}
              </span>
            </b> */}
            <b>
              From $200
              <span style={{color:'grey', textDecoration:'line-through', marginLeft:'20px'}}>
                $85.99
              </span>
            </b>
          </div>
          <div className="product-page-description">
            {/* <p>
              {this.props.productData.description}
            </p> */}
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc egestas ante aliquet consequat feugiat. Mauris pretium ex at orci laoreet, a bibendum eros sodales. Curabitur.
            </p>
          </div>
          <div className="add-to-cart-cta">
            <Button text="Add To Cart" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
